mapper.select:
select *
from pin
WHERE abs(ST_DISTANCE_SPHERE(POINT(#{longitude}, #{latitude}), POINT(longitude, latitude)))  <=  #{radius};

mapper.insert:

INSERT INTO pin (
	name
	, type
	, latitude
	, longitude
	, address
	, comment
	, like
	, user_id
	, crt_date
	, upd_date
) values ( 
	#{name}
	, #{type}
	, #{latitude}
	, #{longitude}
	, #{address}
	, #{comment}
	, #{like}
	, #{userId}
 	, current_timestamp
 	, current_timestamp
 )

mapper.update:
UPDATE pin
SET upd_date = current_timestamp
	, name = #{name}
	, comment = #{comment}
	, like = #{like}
	, user_id = #{userId}
WHERE id = #{id}
